<?php/** * Created by PhpStorm. * User: Administrator * Date: 2019/3/26 * Time: 10:24 */namespace app\admin\controller;use app\admin\model\system\SystemAdmin;use think\Config;use think\Controller;use think\Session;class Common extends  Controller{    protected static $adminInfo;    public  function  _initialize()    {        parent::_initialize();        self::$adminInfo  =  SystemAdmin::activeAdminInfoOrFail();        if(!is_dir('./tmp/'))mkdir ('./tmp/', 0700);        session_save_path('./tmp/');        if(!SystemAdmin::hasActiveAdmin()) return $this->redirect(url('Sign/signin'));        //右侧导航        $admin_nav = Config::get('nav_admin');        $this->assign([            'adminInfo'  =>   self::$adminInfo ,            'admin_nav' => $admin_nav        ]);    }}